import React from "react";

type ClientDetailCardProps = {
  clientName: string;
  clientId: string;
  logo?: React.ReactNode;
  revenue: string;
  revenueLabel?: string;
  trend?: string;
  onRefresh?: () => void;
  isRefreshing?: boolean;
  secondaryCards?: Array<{
    title: string;
    icon?: React.ReactNode;
    content: string;
  }>;
};

export default function ClientDetailCard({
  clientName,
  clientId,
  logo,
  revenue,
  revenueLabel = "Revenue",
  trend,
  onRefresh,
  isRefreshing = false,
  secondaryCards = [],
}: ClientDetailCardProps) {
  // Default logo if not provided - curved S/ribbon style (flowing ribbon)
  const defaultLogo = (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
      {/* Flowing ribbon/curved S shape */}
      <path
        d="M12 12 Q20 8, 28 12 Q32 14, 32 18 Q32 22, 28 24 Q24 26, 20 24 Q16 22, 16 18 Q16 14, 20 12 Q24 10, 28 12 Q32 14, 32 18"
        stroke="#1F2937"
        strokeWidth="2.5"
        strokeLinecap="round"
        fill="none"
      />
      <path
        d="M28 28 Q20 32, 12 28 Q8 26, 8 22 Q8 18, 12 16 Q16 14, 20 16 Q24 18, 24 22 Q24 26, 20 28 Q16 30, 12 28 Q8 26, 8 22"
        stroke="#1F2937"
        strokeWidth="2.5"
        strokeLinecap="round"
        fill="none"
      />
      {/* Yellow dot accent */}
      <circle cx="20" cy="18" r="2.5" fill="#FBBF24" />
    </svg>
  );

  return (
    <div className="space-y-4">
      {/* Main Client Card */}
      <div className="bg-white rounded-2xl shadow-md border border-gray-100 p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            {/* Logo */}
            <div className="w-10 h-10 flex items-center justify-center text-gray-700">
              {logo || defaultLogo}
            </div>
            {/* Name and ID */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900">{clientName}</h3>
              <p className="text-xs text-gray-500 mt-0.5">ID: {clientId}</p>
            </div>
          </div>
          {/* Refresh Button */}
          <button
            type="button"
            onClick={onRefresh}
            className="w-8 h-8 rounded-full bg-gray-100 hover:bg-gray-200 flex items-center justify-center transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500"
            aria-label="Refresh"
          >
            <svg
              className={`w-4 h-4 text-gray-600 ${isRefreshing ? "animate-spin" : ""}`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
              />
            </svg>
          </button>
        </div>

        {/* Revenue/Metric */}
        <div className="flex items-end justify-between">
          <div>
            <p className="text-sm text-gray-500 mb-1">{revenueLabel}</p>
            <div className="flex items-center gap-3">
              <h2 className="text-4xl font-bold text-gray-900">{revenue}</h2>
              {/* Green checkmark indicator */}
              <div className="w-6 h-6 rounded-full bg-green-100 flex items-center justify-center">
                <svg
                  className="w-4 h-4 text-green-600"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M5 13l4 4L19 7"
                  />
                </svg>
              </div>
            </div>
            {trend && (
              <p className="text-xs text-gray-500 mt-1">{trend}</p>
            )}
          </div>
        </div>
      </div>

      {/* Secondary Cards */}
      {secondaryCards.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {secondaryCards.map((card, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl shadow-md border border-gray-100 p-4"
            >
              <div className="flex items-start gap-3">
                {/* Icon */}
                {card.icon && (
                  <div className="w-8 h-8 flex items-center justify-center text-gray-600 flex-shrink-0">
                    {card.icon}
                  </div>
                )}
                {/* Content */}
                <div className="flex-1">
                  <h4 className="text-sm font-semibold text-gray-900 mb-1">{card.title}</h4>
                  <p className="text-xs text-gray-600">{card.content}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
