import React from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";

type Client = { name: string; id: string };

const clients: Client[] = [
  { name: "leads", id: "1342" },
  { name: "tasks", id: "8765" },
  { name: "pipelines", id: "5732" },
  { name: "clients", id: "9864" },
  { name: "onboarding", id: "9632" },
  { name: "performance tracker", id: "2451" },
  { name: "analytics", id: "3892" },
  { name: "reports", id: "4567" },
  { name: "documents/notes", id: "5123" },
  { name: "admin", id: "6789" },
];

type Props = {
  selectedId?: string;
  onSelect?: (id: string) => void;
  onLeadsOptionSelect?: (option: string | null) => void;
  selectedLeadsOption?: string | null;
  onTasksOptionSelect?: (option: string | null) => void;
  selectedTasksOption?: string | null;
  onPipelinesOptionSelect?: (option: string | null) => void;
  selectedPipelinesOption?: string | null;
  onClientsOptionSelect?: (option: string | null) => void;
  selectedClientsOption?: string | null;
  onOnboardingOptionSelect?: (option: string | null) => void;
  selectedOnboardingOption?: string | null;
};

export default function ClientTabsRow({ selectedId, onSelect, onLeadsOptionSelect, selectedLeadsOption: externalSelectedLeadsOption, onTasksOptionSelect, selectedTasksOption: externalSelectedTasksOption, onPipelinesOptionSelect, selectedPipelinesOption: externalSelectedPipelinesOption, onClientsOptionSelect, selectedClientsOption: externalSelectedClientsOption, onOnboardingOptionSelect, selectedOnboardingOption: externalSelectedOnboardingOption }: Props) {
  const [isLeadsMenuOpen, setIsLeadsMenuOpen] = React.useState(false);
  const [isTasksMenuOpen, setIsTasksMenuOpen] = React.useState(false);
  const [isPipelinesMenuOpen, setIsPipelinesMenuOpen] = React.useState(false);
  const [isClientsMenuOpen, setIsClientsMenuOpen] = React.useState(false);
  const [isOnboardingMenuOpen, setIsOnboardingMenuOpen] = React.useState(false);
  const [currentIndex, setCurrentIndex] = React.useState(0);
  const cardsPerView = 5;
  const scrollContainerRef = React.useRef<HTMLDivElement>(null);
  const cardRefs = React.useRef<Record<string, HTMLDivElement | null>>({});
  const [internalSelectedLeadsOption, setInternalSelectedLeadsOption] = React.useState<string | null>("All Leads");
  const [internalSelectedTasksOption, setInternalSelectedTasksOption] = React.useState<string | null>("My Task");
  const [internalSelectedPipelinesOption, setInternalSelectedPipelinesOption] = React.useState<string | null>(null);
  const [internalSelectedClientsOption, setInternalSelectedClientsOption] = React.useState<string | null>(null);
  const [internalSelectedOnboardingOption, setInternalSelectedOnboardingOption] = React.useState<string | null>(null);
  const selectedLeadsOption = externalSelectedLeadsOption !== undefined ? externalSelectedLeadsOption : internalSelectedLeadsOption;
  const selectedTasksOption = externalSelectedTasksOption !== undefined ? externalSelectedTasksOption : internalSelectedTasksOption;
  const selectedPipelinesOption = externalSelectedPipelinesOption !== undefined ? externalSelectedPipelinesOption : internalSelectedPipelinesOption;
  const selectedClientsOption = externalSelectedClientsOption !== undefined ? externalSelectedClientsOption : internalSelectedClientsOption;
  const selectedOnboardingOption = externalSelectedOnboardingOption !== undefined ? externalSelectedOnboardingOption : internalSelectedOnboardingOption;

  const leadsOptions = ["All Leads", "My Leads", "Import Leads"];
  const tasksOptions = ["Team Task", "My Task"];
  const pipelinesOptions = ["Staffing Pipeline", "SaaS Pipeline", "EdTech Pipeline"];
  const clientsOptions = ["My Client", "All Client"];
  const onboardingOptions = ["Pending", "Completed", "Upload Agreements/KYC"];

  const handleLeadsClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsLeadsMenuOpen(!isLeadsMenuOpen);
    if (onSelect) {
      onSelect("1342");
    }
    // When clicking on Leads card directly (not selecting an option), pass null
    if (onLeadsOptionSelect && !isLeadsMenuOpen) {
      onLeadsOptionSelect(null);
    }
  };

  const handleOptionClick = (option: string, e: React.MouseEvent) => {
    e.stopPropagation();
    if (externalSelectedLeadsOption === undefined) {
      setInternalSelectedLeadsOption(option);
    }
    if (onLeadsOptionSelect) {
      onLeadsOptionSelect(option);
    }
    setIsLeadsMenuOpen(false);
  };

  // Close menu when clicking outside
  React.useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as HTMLElement;
      if (!target.closest('.leads-menu-wrapper')) {
        setIsLeadsMenuOpen(false);
      }
      if (!target.closest('.tasks-menu-wrapper')) {
        setIsTasksMenuOpen(false);
      }
      if (!target.closest('.pipelines-menu-wrapper')) {
        setIsPipelinesMenuOpen(false);
      }
      if (!target.closest('.clients-menu-wrapper')) {
        setIsClientsMenuOpen(false);
      }
      if (!target.closest('.onboarding-menu-wrapper')) {
        setIsOnboardingMenuOpen(false);
      }
    };

    if (isLeadsMenuOpen || isTasksMenuOpen || isPipelinesMenuOpen || isClientsMenuOpen || isOnboardingMenuOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isLeadsMenuOpen, isTasksMenuOpen, isPipelinesMenuOpen, isClientsMenuOpen, isOnboardingMenuOpen]);

  const maxIndex = Math.max(0, clients.length - cardsPerView);

  const scrollLeft = () => {
    setCurrentIndex((prev) => Math.max(0, prev - 1));
  };

  const scrollRight = () => {
    setCurrentIndex((prev) => Math.min(maxIndex, prev + 1));
  };

  // Auto-scroll to show selected card
  React.useEffect(() => {
    if (selectedId) {
      const selectedIndex = clients.findIndex((c) => c.id === selectedId);
      if (selectedIndex !== -1) {
        if (selectedIndex < currentIndex) {
          setCurrentIndex(selectedIndex);
        } else if (selectedIndex >= currentIndex + cardsPerView) {
          setCurrentIndex(Math.max(0, selectedIndex - cardsPerView + 1));
        }
      }
    }
  }, [selectedId, currentIndex]);

  return (
    <div className="relative mt-4" style={{ overflow: 'visible' }}>
      {/* Left Navigation Button */}
      {currentIndex > 0 && (
        <button
          onClick={scrollLeft}
          className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-4 z-20 w-10 h-10 rounded-full bg-white/80 backdrop-blur-sm border border-white/50 shadow-lg flex items-center justify-center hover:bg-white/90 transition-all duration-300 hover:scale-110"
          aria-label="Scroll left"
        >
          <ChevronLeft size={20} className="text-gray-700" />
        </button>
      )}

      {/* Right Navigation Button */}
      {currentIndex < maxIndex && (
        <button
          onClick={scrollRight}
          className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-4 z-20 w-10 h-10 rounded-full bg-white/80 backdrop-blur-sm border border-white/50 shadow-lg flex items-center justify-center hover:bg-white/90 transition-all duration-300 hover:scale-110"
          aria-label="Scroll right"
        >
          <ChevronRight size={20} className="text-gray-700" />
        </button>
      )}

      <div className="relative w-full" style={{ overflowX: 'hidden', overflowY: 'visible' }}>
        <div 
          ref={scrollContainerRef}
          className="flex gap-3 transition-transform duration-500 ease-in-out"
          style={{
            width: `${(clients.length / cardsPerView) * 100}%`,
            transform: `translateX(-${currentIndex * (100 / cardsPerView)}%)`,
            willChange: 'transform',
          }}
        >
        {clients.map((c, i) => {
          const isLeads = c.id === "1342";
          const isTasks = c.id === "8765";
          const isPipelines = c.id === "5732";
          const isClients = c.id === "9864";
          const isOnboarding = c.id === "9632";

          const handleTasksClick = (e: React.MouseEvent) => {
            e.stopPropagation();
            setIsTasksMenuOpen(!isTasksMenuOpen);
            if (onSelect) {
              onSelect("8765");
            }
            // When clicking on Tasks card directly (not selecting an option), pass null
            if (onTasksOptionSelect && !isTasksMenuOpen) {
              onTasksOptionSelect(null);
            }
          };

          const handleTasksOptionClick = (option: string, e: React.MouseEvent) => {
            e.stopPropagation();
            if (externalSelectedTasksOption === undefined) {
              setInternalSelectedTasksOption(option);
            }
            if (onTasksOptionSelect) {
              onTasksOptionSelect(option);
            }
            setIsTasksMenuOpen(false);
          };

          const handlePipelinesClick = (e: React.MouseEvent) => {
            e.stopPropagation();
            setIsPipelinesMenuOpen(!isPipelinesMenuOpen);
            if (onSelect) {
              onSelect("5732");
            }
            // When clicking on Pipelines card directly (not selecting an option), pass null
            if (onPipelinesOptionSelect && !isPipelinesMenuOpen) {
              onPipelinesOptionSelect(null);
            }
          };

          const handlePipelinesOptionClick = (option: string, e: React.MouseEvent) => {
            e.stopPropagation();
            if (externalSelectedPipelinesOption === undefined) {
              setInternalSelectedPipelinesOption(option);
            }
            if (onPipelinesOptionSelect) {
              onPipelinesOptionSelect(option);
            }
            setIsPipelinesMenuOpen(false);
          };

          const handleClientsClick = (e: React.MouseEvent) => {
            e.stopPropagation();
            setIsClientsMenuOpen(!isClientsMenuOpen);
            if (onSelect) {
              onSelect("9864");
            }
            // When clicking on Clients card directly (not selecting an option), pass null
            if (onClientsOptionSelect && !isClientsMenuOpen) {
              onClientsOptionSelect(null);
            }
          };

          const handleClientsOptionClick = (option: string, e: React.MouseEvent) => {
            e.stopPropagation();
            if (externalSelectedClientsOption === undefined) {
              setInternalSelectedClientsOption(option);
            }
            if (onClientsOptionSelect) {
              onClientsOptionSelect(option);
            }
            setIsClientsMenuOpen(false);
          };

          const handleOnboardingClick = (e: React.MouseEvent) => {
            e.stopPropagation();
            setIsOnboardingMenuOpen(!isOnboardingMenuOpen);
            if (onSelect) {
              onSelect("9632");
            }
            // When clicking on Onboarding card directly (not selecting an option), pass null
            if (onOnboardingOptionSelect && !isOnboardingMenuOpen) {
              onOnboardingOptionSelect(null);
            }
          };

          const handleOnboardingOptionClick = (option: string, e: React.MouseEvent) => {
            e.stopPropagation();
            if (externalSelectedOnboardingOption === undefined) {
              setInternalSelectedOnboardingOption(option);
            }
            if (onOnboardingOptionSelect) {
              onOnboardingOptionSelect(option);
            }
            setIsOnboardingMenuOpen(false);
          };

          return (
            <div key={c.id} ref={(el) => { if (el) cardRefs.current[c.id] = el; }} className={`relative flex-shrink-0 ${isLeads ? 'leads-menu-wrapper' : isTasks ? 'tasks-menu-wrapper' : isPipelines ? 'pipelines-menu-wrapper' : isClients ? 'clients-menu-wrapper' : isOnboarding ? 'onboarding-menu-wrapper' : ''}`} style={{ width: `${100 / clients.length}%` }}>
              <button
                type="button"
                aria-label={`Open ${c.name} (${c.id})`}
                className={
                  (selectedId === c.id
                    ? "h-16 rounded-2xl bg-white/70 border border-white/80 shadow-lg text-white backdrop-blur flex items-center justify-between px-4 cursor-pointer focus:outline-none w-full"
                    : "h-16 rounded-2xl bg-white/25 hover:bg-white/35 border border-white/50 shadow-sm text-white/90 backdrop-blur flex items-center justify-between px-4 cursor-pointer focus:outline-none opacity-80 hover:opacity-100 w-full")
                }
                onClick={isLeads ? handleLeadsClick : isTasks ? handleTasksClick : isPipelines ? handlePipelinesClick : isClients ? handleClientsClick : isOnboarding ? handleOnboardingClick : () => onSelect && onSelect(c.id)}
              >
                <div className="flex items-center gap-2">
                  <div className="w-6 h-6 rounded-full bg-white/60" />
                  <div className="text-xs text-left">
                    <p className="font-medium text-white drop-shadow">{c.name}</p>
                    <p className="text-[10px] text-white/80 drop-shadow">ID: {c.id}</p>
                  </div>
                </div>
                <div className="w-6 h-6 rounded-full bg-white/40 flex items-center justify-center text-[10px] font-semibold">
                  {c.id === "1342" ? "45" : c.id === "8765" ? "55" : c.id === "5732" ? "32" : c.id === "9864" ? "64" : c.id === "9632" ? "32" : c.id === "2451" ? "28" : c.id === "3892" ? "41" : c.id === "4567" ? "19" : c.id === "5123" ? "37" : "15"}
                </div>
              </button>

              {/* Leads Dropdown Menu - appears BELOW the card */}
              {isLeads && isLeadsMenuOpen && (
                <div className="absolute top-full left-0 mt-2 w-full bg-white rounded-lg shadow-xl border border-gray-200 z-[9999] overflow-hidden min-w-[140px]">
                  {leadsOptions.map((option, idx) => (
                    <button
                      key={idx}
                      type="button"
                      onClick={(e) => handleOptionClick(option, e)}
                      className={`w-full text-left px-4 py-2.5 text-sm transition-colors ${
                        selectedLeadsOption === option
                          ? "bg-gray-100 text-gray-900 font-medium"
                          : "text-gray-700 hover:bg-gray-50"
                      } ${idx !== leadsOptions.length - 1 ? "border-b border-gray-100" : ""}`}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}

              {/* Tasks Dropdown Menu - appears BELOW the card */}
              {isTasks && isTasksMenuOpen && (
                <div className="absolute top-full left-0 mt-2 w-full bg-white rounded-lg shadow-xl border border-gray-200 z-[9999] overflow-hidden min-w-[140px]" style={{ position: 'absolute' }}>
                  {tasksOptions.map((option, idx) => (
                    <button
                      key={idx}
                      type="button"
                      onClick={(e) => handleTasksOptionClick(option, e)}
                      className={`w-full text-left px-4 py-2.5 text-sm transition-colors ${
                        selectedTasksOption === option
                          ? "bg-gray-100 text-gray-900 font-medium"
                          : "text-gray-700 hover:bg-gray-50"
                      } ${idx !== tasksOptions.length - 1 ? "border-b border-gray-100" : ""}`}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}

              {/* Pipelines Dropdown Menu - appears BELOW the card */}
              {isPipelines && isPipelinesMenuOpen && (
                <div className="absolute top-full left-0 mt-2 w-full bg-white rounded-lg shadow-xl border border-gray-200 z-[9999] overflow-hidden min-w-[140px]" style={{ position: 'absolute' }}>
                  {pipelinesOptions.map((option, idx) => (
                    <button
                      key={idx}
                      type="button"
                      onClick={(e) => handlePipelinesOptionClick(option, e)}
                      className={`w-full text-left px-4 py-2.5 text-sm transition-colors ${
                        selectedPipelinesOption === option
                          ? "bg-gray-100 text-gray-900 font-medium"
                          : "text-gray-700 hover:bg-gray-50"
                      } ${idx !== pipelinesOptions.length - 1 ? "border-b border-gray-100" : ""}`}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}

              {/* Clients Dropdown Menu - appears BELOW the card */}
              {isClients && isClientsMenuOpen && (
                <div className="absolute top-full left-0 mt-2 w-full bg-white rounded-lg shadow-xl border border-gray-200 z-[9999] overflow-hidden min-w-[140px]" style={{ position: 'absolute' }}>
                  {clientsOptions.map((option, idx) => (
                    <button
                      key={idx}
                      type="button"
                      onClick={(e) => handleClientsOptionClick(option, e)}
                      className={`w-full text-left px-4 py-2.5 text-sm transition-colors ${
                        selectedClientsOption === option
                          ? "bg-gray-100 text-gray-900 font-medium"
                          : "text-gray-700 hover:bg-gray-50"
                      } ${idx !== clientsOptions.length - 1 ? "border-b border-gray-100" : ""}`}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}

              {/* Onboarding Dropdown Menu - appears BELOW the card */}
              {isOnboarding && isOnboardingMenuOpen && (
                <div className="absolute top-full left-0 mt-2 w-full bg-white rounded-lg shadow-xl border border-gray-200 z-[9999] overflow-hidden min-w-[180px]" style={{ position: 'absolute' }}>
                  {onboardingOptions.map((option, idx) => (
                    <button
                      key={idx}
                      type="button"
                      onClick={(e) => handleOnboardingOptionClick(option, e)}
                      className={`w-full text-left px-4 py-2.5 text-sm transition-colors ${
                        selectedOnboardingOption === option
                          ? "bg-gray-100 text-gray-900 font-medium"
                          : "text-gray-700 hover:bg-gray-50"
                      } ${idx !== onboardingOptions.length - 1 ? "border-b border-gray-100" : ""}`}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}
            </div>
          );
        })}
        </div>
      </div>
    </div>
  );
}
