import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

// Radial fan of glossy capsules that closely mimics the reference
type Props = {
  isLoading?: boolean;
  loadingHueDeg?: number; // base hue when loading starts
  cycleColors?: boolean;  // animate hue rotation while loading
};

export default function RadialDecoration({ isLoading = false, loadingHueDeg = 120, cycleColors = true }: Props) {
  const [progress, setProgress] = React.useState(0);
  const containerRef = useRef<HTMLDivElement>(null);
  const capsulesRef = useRef<HTMLDivElement[]>([]);
  const count = 6; // denser to complete half circle
  // lighter on the left, darker on the right
  const colors = ["#F0F7FF", "#E6F3FF", "#D8EDFF", "#C7E5FF", "#A9D7FF", "#7EC2FF", "#4DA9FA", "#1D83EB"];
  // Bottom half-circle rotated slightly left so ends are near-horizontal
  const start = -180; // rotate left (was around -180)
  const end = 0;    // right end slightly up
  const angles = Array.from({ length: count }, (_, i) => start + (i * (end - start)) / (count - 1));

  // Animate progress from 0 to 100% when loading
  React.useEffect(() => {
    if (isLoading) {
      setProgress(0);
      const duration = 700; // Match the refresh duration
      const startTime = Date.now();
      const interval = setInterval(() => {
        const elapsed = Date.now() - startTime;
        const newProgress = Math.min(100, (elapsed / duration) * 100);
        setProgress(newProgress);
        if (newProgress >= 100) {
          clearInterval(interval);
        }
      }, 16); // ~60fps
      return () => clearInterval(interval);
    } else {
      setProgress(0);
    }
  }, [isLoading]);

  // GSAP 3D animations
  useEffect(() => {
    // Wait for refs to be populated
    const timer = setTimeout(() => {
      if (!containerRef.current || capsulesRef.current.length === 0) return;

      const capsules = capsulesRef.current.filter(Boolean);
      if (capsules.length === 0) return;

      const animations: gsap.core.Tween[] = [];

    // Initial entrance animation - staggered 3D rotation
    capsules.forEach((capsule, i) => {
      const anim = gsap.fromTo(
        capsule,
        {
          rotationY: -90,
          rotationX: -20,
          z: -200,
          opacity: 0,
          scale: 0.5,
        },
        {
          rotationY: 0,
          rotationX: 0,
          z: 0,
          opacity: 1,
          scale: 1,
          duration: 1.2,
          ease: "back.out(1.7)",
          delay: i * 0.1,
        }
      );
      animations.push(anim);
    });

    // Continuous floating animation with 3D rotation
    capsules.forEach((capsule, i) => {
      const anim = gsap.to(capsule, {
        rotationY: 5 + i * 2,
        rotationX: 2 + i,
        y: 10 + i * 3,
        duration: 3 + i * 0.5,
        ease: "sine.inOut",
        repeat: -1,
        yoyo: true,
        delay: i * 0.2,
      });
      animations.push(anim);
    });

    // Mouse interaction for 3D effect
    const handleMouseMove = (e: MouseEvent) => {
      if (!containerRef.current) return;
      const rect = containerRef.current.getBoundingClientRect();
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.top + rect.height / 2;
      const mouseX = e.clientX - centerX;
      const mouseY = e.clientY - centerY;

      capsules.forEach((capsule, i) => {
        const rotateY = (mouseX / rect.width) * 15;
        const rotateX = -(mouseY / rect.height) * 10;
        const z = (mouseX / rect.width) * 30;

        gsap.to(capsule, {
          rotationY: rotateY,
          rotationX: rotateX,
          z: z,
          duration: 0.5,
          ease: "power2.out",
        });
      });
    };

    const handleMouseLeave = () => {
      capsules.forEach((capsule) => {
        gsap.to(capsule, {
          rotationY: 0,
          rotationX: 0,
          z: 0,
          duration: 0.8,
          ease: "power2.out",
        });
      });
    };

      containerRef.current.addEventListener("mousemove", handleMouseMove);
      containerRef.current.addEventListener("mouseleave", handleMouseLeave);

      // Store cleanup function
      const cleanup = () => {
        if (containerRef.current) {
          containerRef.current.removeEventListener("mousemove", handleMouseMove);
          containerRef.current.removeEventListener("mouseleave", handleMouseLeave);
        }
        animations.forEach(anim => anim.kill());
      };

      // Return cleanup from setTimeout callback (not from useEffect)
      // We'll handle cleanup in the useEffect return
      return cleanup;
    }, 100);

    // Cleanup function for useEffect
    return () => {
      clearTimeout(timer);
    };
  }, []);

  return (
    <div 
      ref={containerRef}
      className="relative w-full flex justify-center items-center mt-0"
      style={{ 
        perspective: '1000px',
        perspectiveOrigin: '50% 50%',
        minHeight: '320px',
        overflow: 'visible'
      }}
    >
      {/* 3D Capsules Container */}
      <div 
        className="relative"
        style={{
          width: '740px',
          height: '320px',
          transformStyle: 'preserve-3d',
        }}
      >
        {angles.map((angle, i) => {
          const r = 110;
          const cx = 370 + Math.cos((angle * Math.PI) / 180) * r;
          const cy = 325 + Math.sin((angle * Math.PI) / 180) * r;
          const rot = angle + 90;
          const body = colors[i] || colors[colors.length - 1];
          
          return (
            <div
              key={i}
              ref={(el) => {
                if (el) capsulesRef.current[i] = el;
              }}
              className="absolute"
              style={{
                left: `${cx}px`,
                top: `${cy}px`,
                transform: `translate(-50%, -50%) rotateZ(${rot}deg)`,
                transformStyle: 'preserve-3d',
                transformOrigin: '50% 50%',
              }}
            >
              {/* 3D Pill-shaped Capsule */}
              <div
                className="relative"
                style={{
                  width: '50px',
                  height: '180px',
                  transformStyle: 'preserve-3d',
                }}
              >
                {/* Main body with 3D gradient */}
                <div
                  className="absolute inset-0 rounded-full"
                  style={{
                    background: `linear-gradient(to bottom, 
                      rgba(255, 255, 255, 0.95) 0%,
                      ${body} 30%,
                      ${body} 70%,
                      rgba(255, 255, 255, 0.95) 100%)`,
                    boxShadow: `
                      0 8px 20px rgba(0, 0, 0, 0.18),
                      0 4px 8px rgba(0, 0, 0, 0.12),
                      inset 0 1px 0 rgba(255, 255, 255, 0.9),
                      inset 0 -1px 0 rgba(0, 0, 0, 0.15)
                    `,
                    border: '1px solid rgba(230, 243, 255, 0.7)',
                    filter: 'drop-shadow(0 4px 8px rgba(0, 0, 0, 0.1))',
                  }}
                />
                
                {/* Inner core with depth */}
                <div
                  className="absolute inset-0 rounded-full"
                  style={{
                    left: '12px',
                    right: '12px',
                    top: '8px',
                    bottom: '8px',
                    background: `linear-gradient(to bottom,
                      rgba(185, 224, 255, 0.25) 0%,
                      rgba(47, 143, 233, 0.6) 60%,
                      rgba(10, 95, 192, 0.75) 100%)`,
                    transform: 'translateZ(5px)',
                  }}
                />
                
                {/* Top white cap */}
                <div
                  className="absolute rounded-full"
                  style={{
                    top: '0',
                    left: '0',
                    right: '0',
                    height: '28px',
                    background: 'rgba(255, 255, 255, 0.95)',
                    boxShadow: 'inset 0 2px 4px rgba(255, 255, 255, 0.8)',
                    transform: 'translateZ(8px)',
                  }}
                />
                
                {/* Specular highlight */}
                <div
                  className="absolute rounded-full"
                  style={{
                    left: '0',
                    top: '0',
                    width: '20px',
                    height: '100%',
                    background: `linear-gradient(to right,
                      rgba(255, 255, 255, 0.85) 0%,
                      rgba(255, 255, 255, 0) 100%)`,
                    opacity: 0.45 + (i / (count - 1)) * 0.25,
                    transform: 'translateZ(10px)',
                  }}
                />
                
                {/* Progress bar overlay when loading */}
                {isLoading && progress > 0 && (
                  <div
                    className="absolute rounded-full overflow-hidden"
                    style={{
                      top: '8px',
                      left: '0',
                      width: `${Math.min(100, (progress / 100) * 100)}%`,
                      height: 'calc(100% - 16px)',
                      background: `linear-gradient(to right,
                        rgba(255, 255, 255, 0.95) 0%,
                        rgba(147, 197, 253, 0.9) 50%,
                        rgba(96, 165, 250, 0.85) 100%)`,
                      transform: 'translateZ(12px)',
                      opacity: 0.9,
                    }}
                  />
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* SVG for loading arc and other effects */}
      <svg 
        width="740" 
        height="320" 
        viewBox="0 0 740 320" 
        fill="none"
        className="absolute inset-0 pointer-events-none"
        style={{ zIndex: -1 }}
      >
        <defs>
          {/* blue inner core with slight vertical depth */}
          <linearGradient id="capsInner" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#B9E0FF" stopOpacity="0.25" />
            <stop offset="60%" stopColor="#2F8FE9" stopOpacity="0.6" />
            <stop offset="100%" stopColor="#0A5FC0" stopOpacity="0.75" />
          </linearGradient>
          {/* diagonal specular highlight */}
          <linearGradient id="capsSpec" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.85" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          <filter id="soft" x="-50%" y="-50%" width="200%" height="200%">
            <feDropShadow dx="0" dy="8" stdDeviation="10" floodOpacity="0.18" />
          </filter>
          {/* background halo */}
          <radialGradient id="halo" cx="50%" cy="70%" r="60%">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.35" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </radialGradient>
          {/* gradient for loading arc (green -> blue) */}
          <linearGradient id="loadGrad" x1="0" y1="0" x2="1" y2="0">
            <stop offset="0%" stopColor="#22C55E" stopOpacity="0.35" />
            <stop offset="55%" stopColor="#10B981" stopOpacity="0.95" />
            <stop offset="100%" stopColor="#3B82F6" stopOpacity="0.95" />
          </linearGradient>
          {/* Progress bar gradient for cylinders - bright white to blue */}
          <linearGradient id="progressGradBase" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.95" />
            <stop offset="50%" stopColor="#93C5FD" stopOpacity="0.9" />
            <stop offset="100%" stopColor="#60A5FA" stopOpacity="0.85" />
          </linearGradient>
          <filter id="blurGlow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceGraphic" stdDeviation="4" />
          </filter>
        </defs>

        {/* soft halo behind capsules */}
        <ellipse cx="370" cy="250" rx="250" ry="150" fill="url(#halo)" />

        {/* Loading arc overlay following the same half-circle path */}
        {isLoading && (
          <g>
            {/* the path is a half-ellipse matching the soft halo center */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke="url(#loadGrad)"
              strokeWidth="10"
              strokeLinecap="round"

              fill="none"
              className="animate-dash"
              style={{ strokeDasharray: "160 820" }}
            />
            {/* subtle glow under the arc */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke="#22C55E"
              strokeWidth="14"
              strokeLinecap="round"
              opacity="0.12"
              filter="url(#blurGlow)"
              fill="none"
              className="animate-dash"
              style={{ strokeDasharray: "160 820" }}
            />
          </g>
        )}
      </svg>

      {/* local animation keyframes */}
      {isLoading && (
        <style>
          {`
            @keyframes dash-move { to { stroke-dashoffset: -980; } }
            .animate-dash { animation: dash-move 1.8s linear infinite; }
            @keyframes cyl-pulse { 0% { filter: brightness(1) } 50% { filter: hue-rotate(25deg) brightness(1.08) } 100% { filter: brightness(1) } }
            .animate-cyl { animation: cyl-pulse 1.6s ease-in-out infinite; }
          `}
        </style>
      )}
    </div>
  );
}
