import React, { useState } from "react";

interface LoginProps {
  onLogin: (email: string, password: string, role: string) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [selectedRole, setSelectedRole] = useState("");

  // Demo credentials for each role
  const demoCredentials: Record<string, { email: string; password: string }> = {
    Admin: { email: "admin@crm.com", password: "admin123" },
    Manager: { email: "manager@crm.com", password: "manager123" },
    Sales: { email: "sales@crm.com", password: "sales123" },
    RA: { email: "ra@crm.com", password: "ra123" },
    Recruiter: { email: "recruiter@crm.com", password: "recruiter123" },
    Viewer: { email: "viewer@crm.com", password: "viewer123" },
  };

  const roles = ["Admin", "Manager", "Sales", "RA", "Recruiter", "Viewer"];

  const handleRoleClick = (role: string) => {
    const credentials = demoCredentials[role];
    if (credentials) {
      setEmail(credentials.email);
      setPassword(credentials.password);
      setSelectedRole(role);
    }
  };

  // Auto-detect role from email
  const getRoleFromEmail = (email: string): string => {
    if (email.includes("admin@")) return "Admin";
    if (email.includes("manager@")) return "Manager";
    if (email.includes("sales@")) return "Sales";
    if (email.includes("ra@")) return "RA";
    if (email.includes("recruiter@")) return "Recruiter";
    if (email.includes("viewer@")) return "Viewer";
    return selectedRole || "User";
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (email && password) {
      // Auto-detect role from email or use selected role
      const role = getRoleFromEmail(email);
      onLogin(email, password, role);
    }
  };

  const handleRequestAccess = () => {
    // Handle request access logic
    console.log("Request access clicked");
    alert("Access request functionality would be implemented here");
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4" style={{ backgroundColor: "#e9ecef" }}>
      <div className="w-full max-w-md">
        {/* Login Card */}
        <div className="bg-white rounded-lg shadow-lg p-8" style={{ backgroundColor: "#FFFFFF" }}>
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-2xl font-bold mb-2" style={{ color: "#212529" }}>
              CRM System Login
            </h1>
            <p className="text-sm" style={{ color: "#6c757d" }}>
              Sign in to access your role-based dashboard
            </p>
          </div>

          {/* Login Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Email Field */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium mb-2" style={{ color: "#212529" }}>
                Email
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg
                    className="w-5 h-5"
                    style={{ color: "#6c757d" }}
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"
                    />
                  </svg>
                </div>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  className="w-full pl-10 pr-4 py-3 rounded-lg border focus:outline-none focus:ring-2 focus:ring-blue-500"
                  style={{
                    backgroundColor: "#f8f9fa",
                    borderColor: "#dee2e6",
                    color: "#212529",
                    borderRadius: "8px",
                  }}
                  required
                />
              </div>
            </div>

            {/* Password Field */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium mb-2" style={{ color: "#212529" }}>
                Password
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg
                    className="w-5 h-5"
                    style={{ color: "#6c757d" }}
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"
                    />
                  </svg>
                </div>
                <input
                  type={showPassword ? "text" : "password"}
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  className="w-full pl-10 pr-10 py-3 rounded-lg border focus:outline-none focus:ring-2 focus:ring-blue-500"
                  style={{
                    backgroundColor: "#f8f9fa",
                    borderColor: "#dee2e6",
                    color: "#212529",
                    borderRadius: "8px",
                  }}
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                >
                  <svg
                    className="w-5 h-5"
                    style={{ color: "#6c757d" }}
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    {showPassword ? (
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21"
                      />
                    ) : (
                      <>
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
                        />
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"
                        />
                      </>
                    )}
                  </svg>
                </button>
              </div>
            </div>

            {/* Sign In Button */}
            <button
              type="submit"
              className="w-full py-3 rounded-lg font-bold text-white focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-900 transition-colors"
              style={{ backgroundColor: "#212529", borderRadius: "8px" }}
            >
              Sign In
            </button>

            {/* Request Access Button */}
            <button
              type="button"
              onClick={handleRequestAccess}
              className="w-full py-3 rounded-lg font-medium border focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-300 transition-colors flex items-center justify-center gap-2"
              style={{
                backgroundColor: "#FFFFFF",
                borderColor: "#dee2e6",
                color: "#212529",
                borderRadius: "8px",
              }}
            >
              <svg
                className="w-5 h-5"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M18 9v3m0 0v3m0-3h3m-3 0h-3m-2-5a4 4 0 11-8 0 4 4 0 018 0zM3 20a6 6 0 0112 0v1H3v-1z"
                />
              </svg>
              Request Access
            </button>
          </form>

          {/* Demo Credentials Section */}
          <div className="mt-8">
            <p className="text-center text-sm mb-4" style={{ color: "#6c757d" }}>
              Demo Credentials (Click to fill)
            </p>
            <div className="grid grid-cols-2 gap-2">
              {roles.map((role) => (
                <button
                  key={role}
                  type="button"
                  onClick={() => handleRoleClick(role)}
                  className="py-2.5 px-4 rounded-lg border font-medium flex items-center justify-center gap-2 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-300 transition-colors"
                  style={{
                    backgroundColor: selectedRole === role ? "#f8f9fa" : "#FFFFFF",
                    borderColor: "#dee2e6",
                    color: "#212529",
                  }}
                >
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                  {role}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
