import React from "react";

// Radial fan of glossy capsules with enhanced 3D glossy effects and red-to-green light show
type Props = {
  isLoading?: boolean;
  loadingHueDeg?: number;
  cycleColors?: boolean;
};

export default function RadialDecoration({ isLoading = false }: Props) {
  const [progress, setProgress] = React.useState(0);
  const [lightHue, setLightHue] = React.useState(0);
  const count = 6;
  const colors = ["#F0F7FF", "#E6F3FF", "#D8EDFF", "#C7E5FF", "#A9D7FF", "#7EC2FF"];
  const start = -180;
  const end = 0;
  const angles = Array.from({ length: count }, (_, i) => start + (i * (end - start)) / (count - 1));

  // Animate progress from 0 to 100% when loading
  React.useEffect(() => {
    if (isLoading) {
      setProgress(0);
      const duration = 700;
      const startTime = Date.now();
      const interval = setInterval(() => {
        const elapsed = Date.now() - startTime;
        const newProgress = Math.min(100, (elapsed / duration) * 100);
        setProgress(newProgress);
        if (newProgress >= 100) {
          clearInterval(interval);
        }
      }, 16);
      return () => clearInterval(interval);
    } else {
      setProgress(0);
    }
  }, [isLoading]);

  // Animate light hue from red (0) to green (120) during loading
  React.useEffect(() => {
    if (isLoading) {
      let animationFrame: number;
      const duration = 2000;
      const startTime = Date.now();
      
      const animate = () => {
        const elapsed = Date.now() - startTime;
        const progress = (elapsed % duration) / duration;
        const newHue = progress * 120;
        setLightHue(newHue);
        animationFrame = requestAnimationFrame(animate);
      };
      
      animationFrame = requestAnimationFrame(animate);
      return () => cancelAnimationFrame(animationFrame);
    } else {
      setLightHue(0);
    }
  }, [isLoading]);

  return (
    <div className="relative w-full flex justify-center mt-6">
      <svg width="740" height="320" viewBox="0 0 740 320" fill="none">
        <defs>
          {/* Enhanced blue inner core with more depth */}
          <linearGradient id="capsInner" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#D4EBFF" stopOpacity="0.4" />
            <stop offset="30%" stopColor="#A9D7FF" stopOpacity="0.5" />
            <stop offset="60%" stopColor="#4DA9FA" stopOpacity="0.7" />
            <stop offset="100%" stopColor="#1D83EB" stopOpacity="0.85" />
          </linearGradient>
          
          {/* Enhanced diagonal specular highlight - more prominent */}
          <linearGradient id="capsSpec" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.95" />
            <stop offset="40%" stopColor="#FFFFFF" stopOpacity="0.6" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          
          {/* Secondary highlight for extra gloss */}
          <linearGradient id="capsSpec2" x1="0" y1="0" x2="0.3" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.7" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          
          {/* Top highlight for 3D effect */}
          <linearGradient id="capsTop" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.9" />
            <stop offset="50%" stopColor="#FFFFFF" stopOpacity="0.4" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          
          {/* Enhanced shadow filter */}
          <filter id="soft" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceAlpha" stdDeviation="8" />
            <feOffset dx="0" dy="10" result="offsetblur" />
            <feComponentTransfer>
              <feFuncA type="linear" slope="0.25" />
            </feComponentTransfer>
            <feMerge>
              <feMergeNode />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
          
          {/* Enhanced glow filter for light show */}
          <filter id="glow" x="-100%" y="-100%" width="300%" height="300%">
            <feGaussianBlur stdDeviation="6" result="coloredBlur"/>
            <feMerge>
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
          
          {/* Stronger glow for light show effect */}
          <filter id="strongGlow" x="-100%" y="-100%" width="300%" height="300%">
            <feGaussianBlur stdDeviation="8" result="coloredBlur"/>
            <feMerge>
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
          
          {/* Background halo - enhanced */}
          <radialGradient id="halo" cx="50%" cy="70%" r="60%">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.45" />
            <stop offset="50%" stopColor="#E6F3FF" stopOpacity="0.2" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </radialGradient>
          
          {/* Red to green gradient for loading light show */}
          <linearGradient id="loadGrad" x1="0" y1="0" x2="1" y2="0">
            <stop offset="0%" stopColor="#EF4444" stopOpacity="1" />
            <stop offset="33%" stopColor="#F59E0B" stopOpacity="1" />
            <stop offset="66%" stopColor="#EAB308" stopOpacity="1" />
            <stop offset="100%" stopColor="#22C55E" stopOpacity="1" />
          </linearGradient>
          
          {/* Dynamic red-to-green gradient - force update by changing id */}
          <linearGradient id={`lightShowGrad-${Math.floor(lightHue / 5)}`} x1="0" y1="0" x2="1" y2="0">
            <stop offset="0%" stopColor={`hsl(${lightHue}, 100%, 60%)`} stopOpacity="1" />
            <stop offset="50%" stopColor={`hsl(${Math.min(lightHue + 30, 120)}, 100%, 65%)`} stopOpacity="1" />
            <stop offset="100%" stopColor={`hsl(${Math.min(lightHue + 60, 120)}, 100%, 60%)`} stopOpacity="1" />
          </linearGradient>
          
          {/* Progress bar gradient */}
          <linearGradient id={`progressGrad-${Math.floor(lightHue / 5)}`} x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor={`hsl(${lightHue}, 100%, 70%)`} stopOpacity="1" />
            <stop offset="50%" stopColor={`hsl(${Math.min(lightHue + 60, 120)}, 100%, 75%)`} stopOpacity="1" />
            <stop offset="100%" stopColor={`hsl(${Math.min(lightHue + 120, 120)}, 100%, 70%)`} stopOpacity="1" />
          </linearGradient>
          
          {/* Glow filters for cylinders */}
          {Array.from({ length: count }, (_, i) => {
            const hue = (lightHue + (i * 20)) % 120;
            return (
              <React.Fragment key={`filters-${i}`}>
                <filter id={`lightGlow-${i}`} x="-100%" y="-100%" width="300%" height="300%">
                  <feGaussianBlur stdDeviation="4" result="coloredBlur"/>
                  <feFlood floodColor={`hsl(${hue}, 100%, 60%)`} floodOpacity="0.6"/>
                  <feComposite in2="coloredBlur" operator="in"/>
                  <feMerge>
                    <feMergeNode/>
                    <feMergeNode in="SourceGraphic"/>
                  </feMerge>
                </filter>
                <filter id={`progressGlow-${i}`} x="-100%" y="-100%" width="300%" height="300%">
                  <feGaussianBlur stdDeviation="6" result="coloredBlur"/>
                  <feFlood floodColor={`hsl(${hue}, 100%, 70%)`} floodOpacity="0.8"/>
                  <feComposite in2="coloredBlur" operator="in"/>
                  <feMerge>
                    <feMergeNode/>
                    <feMergeNode in="SourceGraphic"/>
                  </feMerge>
                </filter>
                <filter id={`edgeGlow-${i}`} x="-100%" y="-100%" width="300%" height="300%">
                  <feGaussianBlur stdDeviation="5" result="coloredBlur"/>
                  <feFlood floodColor={`hsl(${hue}, 100%, 85%)`} floodOpacity="1"/>
                  <feComposite in2="coloredBlur" operator="in"/>
                  <feMerge>
                    <feMergeNode/>
                    <feMergeNode in="SourceGraphic"/>
                  </feMerge>
                </filter>
              </React.Fragment>
            );
          })}
          
          {/* Arc glow filter */}
          <filter id={`arcGlow-${Math.floor(lightHue / 5)}`} x="-100%" y="-100%" width="300%" height="300%">
            <feGaussianBlur stdDeviation="5" result="coloredBlur"/>
            <feFlood floodColor={`hsl(${lightHue}, 100%, 65%)`} floodOpacity="0.9"/>
            <feComposite in2="coloredBlur" operator="in"/>
            <feMerge>
              <feMergeNode/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
          
          <filter id="blurGlow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceGraphic" stdDeviation="6" />
          </filter>
          
          {/* Reflection filter for extra gloss */}
          <filter id="reflection" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceGraphic" stdDeviation="2" />
            <feComponentTransfer>
              <feFuncA type="linear" slope="0.3" />
            </feComponentTransfer>
          </filter>
        </defs>

        {/* soft halo behind capsules */}
        <ellipse cx="370" cy="250" rx="250" ry="150" fill="url(#halo)" />

        {angles.map((angle, i) => {
          const r = 110;
          const cx = 370 + Math.cos((angle * Math.PI) / 180) * r;
          const cy = 325 + Math.sin((angle * Math.PI) / 180) * r;
          const rot = angle + 90;
          const body = colors[i] || colors[colors.length - 1];
          const sideOpacity = 0.5 + (i / (count - 1)) * 0.3; // more prominent highlights
          const progressWidth = (progress / 100) * 50; // 50 is the width of the cylinder
          const lightDelay = (i / count) * 0.3; // Stagger the light show
          const currentLightHue = (lightHue + (lightDelay * 120)) % 120;
          
          return (
            <g key={i} transform={`translate(${cx}, ${cy}) rotate(${rot})`} filter="url(#soft)" className={isLoading ? "animate-cyl" : undefined}>
              {/* Enhanced glass body with gradient */}
              <rect x={-23} y={-92} rx={18} width={50} height={180} fill={body} stroke="#D8EDFF" strokeWidth={1.5} opacity={0.95} />
              
              {/* Inner core with enhanced depth */}
              <g transform="skewX(-10)">
                <rect x={-14} y={-75} rx={12} width={32} height={140} fill="url(#capsInner)" />
              </g>
              
              {/* Light show effect - red to green animation */}
              {isLoading && (
                <rect 
                  x={-23} 
                  y={-92} 
                  rx={18} 
                  width={50} 
                  height={180} 
                  fill={`hsl(${currentLightHue}, 85%, 65%)`}
                  opacity={0.5}
                  filter={`url(#lightGlow-${i})`}
                />
              )}
              
              {/* Progress bar overlay - red to green light show */}
              {isLoading && progress > 0 && (
                <g>
                  {/* Progress fill with animated red-to-green gradient */}
                  <rect 
                    x={-23} 
                    y={-64} 
                    rx={18} 
                    width={Math.min(50, progressWidth)} 
                    height={152} 
                    fill={`url(#progressGrad-${Math.floor(lightHue / 5)})`}
                    opacity={0.95}
                    filter={`url(#progressGlow-${i})`}
                  />
                  {/* Bright glowing edge at progress front */}
                  {progressWidth < 50 && (
                    <rect 
                      x={-23 + Math.min(50, progressWidth) - 2} 
                      y={-64} 
                      rx={18} 
                      width={6} 
                      height={152} 
                      fill={`hsl(${currentLightHue}, 100%, 85%)`}
                      opacity={1}
                      filter={`url(#edgeGlow-${i})`}
                    />
                  )}
                </g>
              )}
              
              {/* Enhanced white rounded cap with gradient */}
              <rect x={-23} y={-92} rx={18} width={50} height={28} fill="url(#capsTop)" opacity={0.98} />
              
              {/* Primary specular highlight - more prominent */}
              <rect x={-23} y={-92} rx={18} width={22} height={160} fill="url(#capsSpec)" opacity={sideOpacity} />
              
              {/* Secondary highlight for extra gloss */}
              <rect x={-18} y={-85} rx={14} width={12} height={140} fill="url(#capsSpec2)" opacity={0.6} />
              
              {/* Top rim highlight for 3D effect */}
              <rect x={-20} y={-92} rx={16} width={40} height={12} fill="#FFFFFF" opacity={0.8} />
              
              {/* Bottom shadow for depth */}
              <rect x={-22} y={85} rx={17} width={46} height={8} fill="#1D83EB" opacity={0.15} />
            </g>
          );
        })}

        {/* Enhanced loading arc overlay with red-to-green light show */}
        {isLoading && (
          <>
            {/* Main animated arc with red-to-green gradient */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke="url(#lightShowGrad)"
              strokeWidth="12"
              strokeLinecap="round"
              fill="none"
              className="animate-dash"
              strokeDasharray="160 820"
              filter="url(#arcGlow)"
            />
            {/* Enhanced glow under the arc */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke={`hsl(${lightHue}, 100%, 65%)`}
              strokeWidth="18"
              strokeLinecap="round"
              opacity="0.4"
              filter="url(#blurGlow)"
              fill="none"
              className="animate-dash"
              strokeDasharray="160 820"
            />
            {/* Additional outer glow for more intensity */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke={`hsl(${lightHue}, 100%, 70%)`}
              strokeWidth="22"
              strokeLinecap="round"
              opacity="0.2"
              filter="url(#blurGlow)"
              fill="none"
              className="animate-dash"
              strokeDasharray="160 820"
            />
          </>
        )}
      </svg>

      {/* Enhanced animation keyframes with glossy effects */}
      {isLoading && (
        <style>
          {`
            @keyframes dash-move { 
              to { stroke-dashoffset: -980; } 
            }
            .animate-dash { 
              animation: dash-move 1.8s linear infinite; 
            }
            @keyframes cyl-pulse { 
              0% { 
                filter: brightness(1) drop-shadow(0 0 4px rgba(255, 255, 255, 0.3));
                transform: scale(1);
              } 
              50% { 
                filter: brightness(1.15) drop-shadow(0 0 12px rgba(255, 255, 255, 0.6));
                transform: scale(1.02);
              } 
              100% { 
                filter: brightness(1) drop-shadow(0 0 4px rgba(255, 255, 255, 0.3));
                transform: scale(1);
              } 
            }
            .animate-cyl { 
              animation: cyl-pulse 2s ease-in-out infinite; 
            }
            @keyframes light-wave {
              0% { opacity: 0.3; }
              50% { opacity: 0.8; }
              100% { opacity: 0.3; }
            }
          `}
        </style>
      )}
    </div>
  );
}
