// @ts-nocheck
import React, { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import * as THREE from "three";

// 3D Capsule Component with enhanced visuals
function Capsule3D({ 
  position, 
  rotation, 
  color, 
  index, 
  isLoading, 
  progress 
}: { 
  position: [number, number, number]; 
  rotation: number;
  color: string;
  index: number;
  isLoading: boolean;
  progress: number;
}) {
  const meshRef = useRef<THREE.Mesh>(null);
  const groupRef = useRef<THREE.Group>(null);

  // Continuous floating and rotation animation
  useFrame((state) => {
    if (groupRef.current) {
      // Gentle floating
      groupRef.current.position.y = position[1] + Math.sin(state.clock.elapsedTime * 0.8 + index) * 0.15;
      // Subtle rotation
      groupRef.current.rotation.y = rotation + Math.sin(state.clock.elapsedTime * 0.5 + index) * 0.1;
      groupRef.current.rotation.x = Math.sin(state.clock.elapsedTime * 0.6 + index) * 0.05;
    }
    if (meshRef.current) {
      // Slight scale pulse
      const scale = 1 + Math.sin(state.clock.elapsedTime * 1.2 + index) * 0.02;
      meshRef.current.scale.set(1, scale, 1);
    }
  });

  // Convert hex to RGB
  const hexToRgb = (hex: string) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result
      ? {
          r: parseInt(result[1], 16) / 255,
          g: parseInt(result[2], 16) / 255,
          b: parseInt(result[3], 16) / 255,
        }
      : { r: 0.9, g: 0.95, b: 1 };
  };

  const rgb = hexToRgb(color);
  const materialColor = new THREE.Color(rgb.r, rgb.g, rgb.b);

  return (
    <group ref={groupRef} position={position} rotation={[0, rotation, 0]}>
      {/* Main capsule body - cylinder */}
      <mesh ref={meshRef} castShadow receiveShadow>
        <cylinderGeometry args={[0.5, 0.5, 1.8, 32]} />
        <meshStandardMaterial
          color={materialColor}
          metalness={0.4}
          roughness={0.15}
          envMapIntensity={1.2}
          emissive={materialColor}
          emissiveIntensity={0.1}
        />
      </mesh>
      
      {/* Top sphere cap with glow */}
      <mesh position={[0, 0.9, 0]} castShadow>
        <sphereGeometry args={[0.5, 32, 32, 0, Math.PI * 2, 0, Math.PI / 2]} />
        <meshStandardMaterial
          color="#ffffff"
          metalness={0.6}
          roughness={0.1}
          emissive="#ffffff"
          emissiveIntensity={0.2}
        />
      </mesh>

      {/* Bottom sphere cap */}
      <mesh position={[0, -0.9, 0]} castShadow>
        <sphereGeometry args={[0.5, 32, 32, 0, Math.PI * 2, Math.PI / 2, Math.PI / 2]} />
        <meshStandardMaterial
          color="#ffffff"
          metalness={0.6}
          roughness={0.1}
          emissive="#ffffff"
          emissiveIntensity={0.2}
        />
      </mesh>

      {/* Progress bar overlay when loading */}
      {isLoading && progress > 0 && (
        <mesh position={[0, 0, 0.52]}>
          <boxGeometry args={[1, (progress / 100) * 1.8, 0.05]} />
          <meshStandardMaterial
            color="#60A5FA"
            emissive="#93C5FD"
            emissiveIntensity={0.8}
            metalness={0.3}
            roughness={0.2}
          />
        </mesh>
      )}

      {/* Specular highlight with glow */}
      <mesh position={[-0.3, 0, 0.45]}>
        <boxGeometry args={[0.2, 1.6, 0.1]} />
        <meshStandardMaterial
          color="#ffffff"
          transparent
          opacity={0.5 + (index / 5) * 0.3}
          metalness={0.9}
          roughness={0.05}
          emissive="#ffffff"
          emissiveIntensity={0.3}
        />
      </mesh>

      {/* Glow effect around capsule */}
      <mesh position={[0, 0, 0]}>
        <cylinderGeometry args={[0.55, 0.55, 1.9, 32]} />
        <meshStandardMaterial
          color={materialColor}
          transparent
          opacity={0.2}
          emissive={materialColor}
          emissiveIntensity={0.5}
          side={THREE.BackSide}
        />
      </mesh>
    </group>
  );
}

// Radial fan of glossy capsules that closely mimics the reference
type Props = {
  isLoading?: boolean;
  loadingHueDeg?: number; // base hue when loading starts
  cycleColors?: boolean;  // animate hue rotation while loading
};

export default function RadialDecoration({ isLoading = false, loadingHueDeg = 120, cycleColors = true }: Props) {
  const [progress, setProgress] = React.useState(0);
  const count = 6; // denser to complete half circle
  // lighter on the left, darker on the right
  const colors = ["#F0F7FF", "#E6F3FF", "#D8EDFF", "#C7E5FF", "#A9D7FF", "#7EC2FF", "#4DA9FA", "#1D83EB"];
  // Bottom half-circle rotated slightly left so ends are near-horizontal
  const start = -180; // rotate left (was around -180)
  const end = 0;    // right end slightly up
  const angles = Array.from({ length: count }, (_, i) => start + (i * (end - start)) / (count - 1));

  // Animate progress from 0 to 100% when loading
  React.useEffect(() => {
    if (isLoading) {
      setProgress(0);
      const duration = 700; // Match the refresh duration
      const startTime = Date.now();
      const interval = setInterval(() => {
        const elapsed = Date.now() - startTime;
        const newProgress = Math.min(100, (elapsed / duration) * 100);
        setProgress(newProgress);
        if (newProgress >= 100) {
          clearInterval(interval);
        }
      }, 16); // ~60fps
      return () => clearInterval(interval);
    } else {
      setProgress(0);
    }
  }, [isLoading]);

  return (
    <div className="relative w-full flex justify-center items-center mt-0" style={{ minHeight: '320px' }}>
      {/* 3D Canvas for capsules */}
      <div className="absolute inset-0 flex justify-center items-center" style={{ width: '740px', height: '320px', zIndex: 1, pointerEvents: 'none' }}>
        <Canvas
          camera={{ position: [0, 0, 6], fov: 60 }}
          style={{ width: '100%', height: '100%' }}
          gl={{ antialias: true, alpha: true }}
        >
          {/* Enhanced lighting */}
          <ambientLight intensity={0.8} />
          <directionalLight position={[5, 5, 5]} intensity={1.2} castShadow />
          <directionalLight position={[-5, 3, -5]} intensity={0.6} />
          <pointLight position={[0, 5, 0]} intensity={0.8} />
          <pointLight position={[0, -3, 0]} intensity={0.4} color="#B9E0FF" />
          
          {/* Halo glow effect */}
          <mesh position={[0, -1.2, -1]}>
            <sphereGeometry args={[3, 32, 32]} />
            <meshStandardMaterial
              color="#ffffff"
              transparent
              opacity={0.15}
              emissive="#ffffff"
              emissiveIntensity={0.3}
            />
          </mesh>
          
          {angles.map((angle, i) => {
            const r = 2.0;
            const x = Math.cos((angle * Math.PI) / 180) * r;
            const z = Math.sin((angle * Math.PI) / 180) * r;
            const y = -1.2;
            const rot = (angle * Math.PI) / 180;
            const body = colors[i] || colors[colors.length - 1];
            
            return (
              <Capsule3D
                key={i}
                position={[x, y, z]}
                rotation={rot}
                color={body}
                index={i}
                isLoading={isLoading}
                progress={progress}
              />
            );
          })}
        </Canvas>
      </div>

      {/* SVG for loading arc and halo effects */}
      <svg width="740" height="320" viewBox="0 0 740 320" fill="none" style={{ position: 'relative', zIndex: 0 }}>
        <defs>
          {/* blue inner core with slight vertical depth */}
          <linearGradient id="capsInner" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#B9E0FF" stopOpacity="0.25" />
            <stop offset="60%" stopColor="#2F8FE9" stopOpacity="0.6" />
            <stop offset="100%" stopColor="#0A5FC0" stopOpacity="0.75" />
          </linearGradient>
          {/* diagonal specular highlight */}
          <linearGradient id="capsSpec" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.85" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          <filter id="soft" x="-50%" y="-50%" width="200%" height="200%">
            <feDropShadow dx="0" dy="8" stdDeviation="10" floodOpacity="0.18" />
          </filter>
          {/* background halo */}
          <radialGradient id="halo" cx="50%" cy="70%" r="60%">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.35" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </radialGradient>
          {/* gradient for loading arc (green -> blue) */}
          <linearGradient id="loadGrad" x1="0" y1="0" x2="1" y2="0">
            <stop offset="0%" stopColor="#22C55E" stopOpacity="0.35" />
            <stop offset="55%" stopColor="#10B981" stopOpacity="0.95" />
            <stop offset="100%" stopColor="#3B82F6" stopOpacity="0.95" />
          </linearGradient>
          {/* Progress bar gradient for cylinders - bright white to blue */}
          <linearGradient id="progressGradBase" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.95" />
            <stop offset="50%" stopColor="#93C5FD" stopOpacity="0.9" />
            <stop offset="100%" stopColor="#60A5FA" stopOpacity="0.85" />
          </linearGradient>
          <filter id="blurGlow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceGraphic" stdDeviation="4" />
          </filter>
        </defs>

        {/* soft halo behind capsules */}
        <ellipse cx="370" cy="250" rx="250" ry="150" fill="url(#halo)" />

        {angles.map((angle, i) => {
          const r = 110;
          const cx = 370 + Math.cos((angle * Math.PI) / 180) * r;
          const cy = 325 + Math.sin((angle * Math.PI) / 180) * r;
          const rot = angle + 90;
          const body = colors[i] || colors[colors.length - 1];
          const sideOpacity = 0.45 + (i / (count - 1)) * 0.25; // darker towards right
          const progressWidth = (progress / 100) * 50; // 50 is the width of the cylinder
          return (
            <g key={i} transform={`translate(${cx}, ${cy}) rotate(${rot})`} filter="url(#soft)" className={isLoading ? "animate-cyl" : undefined}>
              {/* glass body */}
              <rect x={-23} y={-92} rx={18} width={50} height={180} fill={body} stroke="#E6F3FF" strokeWidth={1} />
              {/* inner core tilted slightly */}
              <g transform="skewX(-10)">
                <rect x={-14} y={-75} rx={12} width={32} height={140} fill="url(#capsInner)" />
              </g>
              {/* Progress bar overlay - fills from left to right */}
              {isLoading && progress > 0 && (
                <g>
                  {/* Progress fill rectangle with gradient - appears on top of body */}
                  <rect 
                    x={-23} 
                    y={-64} 
                    rx={18} 
                    width={Math.min(50, progressWidth)} 
                    height={152} 
                    fill="url(#progressGradBase)"
                    opacity={0.9}
                  />
                  {/* Bright highlight at the progress edge */}
                  {progressWidth < 50 && (
                    <rect 
                      x={-23 + Math.min(50, progressWidth)} 
                      y={-64} 
                      rx={18} 
                      width={5} 
                      height={152} 
                      fill="#FFFFFF"
                      opacity={1}
                    />
                  )}
                </g>
              )}
              {/* white rounded cap - on top */}
              <rect x={-23} y={-92} rx={18} width={50} height={28} fill="#FFFFFF" opacity={0.95} />
              {/* long specular along left edge */}
              <rect x={-23} y={-92} rx={18} width={20} height={160} fill="url(#capsSpec)" opacity={sideOpacity} />
            </g>
          );
        })}

        {/* Loading arc overlay following the same half-circle path */}
        {isLoading && (
          <g>
            {/* the path is a half-ellipse matching the soft halo center */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke="url(#loadGrad)"
              strokeWidth="10"
              strokeLinecap="round"

              fill="none"
              className="animate-dash"
              style={{ strokeDasharray: "160 820" }}
            />
            {/* subtle glow under the arc */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke="#22C55E"
              strokeWidth="14"
              strokeLinecap="round"
              opacity="0.12"
              filter="url(#blurGlow)"
              fill="none"
              className="animate-dash"
              style={{ strokeDasharray: "160 820" }}
            />
          </g>
        )}
      </svg>

      {/* local animation keyframes */}
      {isLoading && (
        <style>
          {`
            @keyframes dash-move { to { stroke-dashoffset: -980; } }
            .animate-dash { animation: dash-move 1.8s linear infinite; }
            @keyframes cyl-pulse { 0% { filter: brightness(1) } 50% { filter: hue-rotate(25deg) brightness(1.08) } 100% { filter: brightness(1) } }
            .animate-cyl { animation: cyl-pulse 1.6s ease-in-out infinite; }
          `}
        </style>
      )}
    </div>
  );
}
