// src/components/DashboardCard.tsx
import { motion } from "framer-motion";
import React, { useState } from "react";

interface CardProps {
  title: string;
  value: string;
  subtitle: string;
  progress?: number;
  isHighlighted?: boolean; // for middle elevated card
  onRefresh?: () => void;
  isRefreshing?: boolean;
  index?: number;
}

const DashboardCard = ({ title, value, subtitle, progress, isHighlighted, onRefresh, isRefreshing, index = 0 }: CardProps) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.div
      initial={{ opacity: 0, y: 50, rotateX: -15, z: -100 }}
      animate={{
        opacity: 1,
        y: isHighlighted ? -40 : 0,
        rotateX: 0,
        z: isHighlighted ? 20 : 0,
        scale: isHighlighted ? 1.05 : 1,
      }}
      transition={{
        duration: 0.8,
        delay: index * 0.15,
        type: "spring",
        stiffness: 100,
      }}
      whileHover={{
        y: isHighlighted ? -50 : -15,
        rotateY: 8,
        rotateX: -5,
        scale: isHighlighted ? 1.08 : 1.03,
        z: 50,
        transition: { duration: 0.3 },
      }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      style={{
        perspective: 1000,
        transformStyle: "preserve-3d",
      }}
      className={`relative w-full rounded-2xl p-6 min-h-[150px] cursor-pointer
        ${
          isHighlighted
            ? "bg-white shadow-2xl border border-white/70 z-20"
            : "bg-white/70 backdrop-blur-md shadow-lg border border-white/40"
        }`}
    >
      {/* 3D Glow Effect */}
      <motion.div
        animate={{
          opacity: isHovered ? (isHighlighted ? 0.4 : 0.2) : 0,
          scale: isHovered ? 1.2 : 1,
        }}
        transition={{ duration: 0.3 }}
        className={`absolute inset-0 rounded-2xl blur-2xl -z-10 ${
          isHighlighted
            ? "bg-gradient-to-br from-blue-500/30 to-purple-500/30"
            : "bg-gradient-to-br from-blue-400/20 to-purple-400/20"
        }`}
      />

      {/* Top Section */}
      <div className="flex justify-between items-center relative z-10">
        <motion.h3
          animate={{ scale: isHovered ? 1.05 : 1 }}
          className="text-base font-medium text-gray-700 drop-shadow-sm"
        >
          {title}
        </motion.h3>
        <motion.button
          whileHover={{ rotate: 180, scale: 1.2 }}
          whileTap={{ scale: 0.9 }}
          className={`text-gray-400 text-sm ${isRefreshing ? "animate-spin" : ""}`}
          aria-label="Refresh"
          onClick={onRefresh}
        >
          ‚ü≥
        </motion.button>
      </div>

      {/* Value */}
      <motion.p
        animate={{ scale: isHovered ? 1.08 : 1 }}
        className="text-3xl font-semibold mt-3 text-gray-900 relative z-10"
      >
        {value}
      </motion.p>
      <p className="text-sm text-gray-400 relative z-10">{subtitle}</p>

      {/* Segmented Progress Bar */}
      {progress !== undefined && (
        <div className="mt-3 w-full relative z-10">
          {(() => {
            const segmentCount = 24;
            const filled = Math.round(((progress || 0) / 100) * segmentCount);
            const segments = Array.from({ length: segmentCount });
            return (
              <div className="flex gap-[2px]">
                {segments.map((_, idx) => (
                  <motion.div
                    key={idx}
                    initial={{ scaleX: 0, opacity: 0 }}
                    animate={{
                      scaleX: 1,
                      opacity: 1,
                      backgroundColor: idx < filled ? "rgba(74, 222, 128, 0.8)" : "rgb(229, 231, 235)",
                    }}
                    transition={{
                      duration: 0.3,
                      delay: index * 0.15 + idx * 0.01,
                    }}
                    className="h-2 flex-1 rounded-[2px]"
                  />
                ))}
              </div>
            );
          })()}
        </div>
      )}

      {/* Bottom Curve (only for center card or when merging) */}
      {isHighlighted && (
        <motion.div
          animate={{
            scaleY: isHovered ? 1.1 : 1,
          }}
          className="absolute -bottom-5 left-1/2 -translate-x-1/2 w-[80%]"
        >
          <div className="h-8 bg-white rounded-t-[30px] shadow-[0_-6px_20px_rgba(0,0,0,0.08)]"></div>
        </motion.div>
      )}

      {/* Shine effect */}
      <motion.div
        animate={{
          x: isHovered ? ["-100%", "200%"] : "-100%",
        }}
        transition={{
          duration: 1,
          repeat: isHovered ? Infinity : 0,
          repeatDelay: 1.5,
        }}
        className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent rounded-2xl -z-0 pointer-events-none"
        style={{ transform: "skewX(-20deg)" }}
      />
    </motion.div>
  );
};

export default DashboardCard;
