import React from "react";

type Props = {
  isLoading?: boolean;
};

export default function RadialDecoration({ isLoading = false }: Props) {
  const [progress, setProgress] = React.useState(0);
  const [lightHue, setLightHue] = React.useState(0);
  const count = 6;
  const colors = ["#F0F7FF", "#E6F3FF", "#D8EDFF", "#C7E5FF", "#A9D7FF", "#7EC2FF"];
  const start = -180;
  const end = 0;
  const angles = Array.from({ length: count }, (_, i) => start + (i * (end - start)) / (count - 1));

  // Animate progress from 0 to 100% when loading
  React.useEffect(() => {
    if (isLoading) {
      setProgress(0);
      const duration = 700;
      const startTime = Date.now();
      const interval = setInterval(() => {
        const elapsed = Date.now() - startTime;
        const newProgress = Math.min(100, (elapsed / duration) * 100);
        setProgress(newProgress);
        if (newProgress >= 100) {
          clearInterval(interval);
        }
      }, 16);
      return () => clearInterval(interval);
    } else {
      setProgress(0);
    }
  }, [isLoading]);

  // Animate light hue from red (0) to green (120) during loading
  React.useEffect(() => {
    if (isLoading) {
      let animationFrame: number;
      const duration = 2000;
      const startTime = Date.now();
      
      const animate = () => {
        const elapsed = Date.now() - startTime;
        const progress = (elapsed % duration) / duration;
        const newHue = progress * 120;
        setLightHue(newHue);
        animationFrame = requestAnimationFrame(animate);
      };
      
      animationFrame = requestAnimationFrame(animate);
      return () => cancelAnimationFrame(animationFrame);
    } else {
      setLightHue(0);
    }
  }, [isLoading]);

  // Calculate HSL color based on hue
  const getHslColor = (hue: number, saturation = 100, lightness = 60) => {
    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  };

  return (
    <div className="relative w-full flex justify-center mt-6">
      <svg width="740" height="320" viewBox="0 0 740 320" fill="none">
        <defs>
          {/* Enhanced blue inner core */}
          <linearGradient id="capsInner" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#D4EBFF" stopOpacity="0.4" />
            <stop offset="30%" stopColor="#A9D7FF" stopOpacity="0.5" />
            <stop offset="60%" stopColor="#4DA9FA" stopOpacity="0.7" />
            <stop offset="100%" stopColor="#1D83EB" stopOpacity="0.85" />
          </linearGradient>
          
          {/* Enhanced diagonal specular highlight */}
          <linearGradient id="capsSpec" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.95" />
            <stop offset="40%" stopColor="#FFFFFF" stopOpacity="0.6" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          
          {/* Secondary highlight for extra gloss */}
          <linearGradient id="capsSpec2" x1="0" y1="0" x2="0.3" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.7" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          
          {/* Top highlight for 3D effect */}
          <linearGradient id="capsTop" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.9" />
            <stop offset="50%" stopColor="#FFFFFF" stopOpacity="0.4" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </linearGradient>
          
          {/* Enhanced shadow filter */}
          <filter id="soft" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceAlpha" stdDeviation="8" />
            <feOffset dx="0" dy="10" result="offsetblur" />
            <feComponentTransfer>
              <feFuncA type="linear" slope="0.25" />
            </feComponentTransfer>
            <feMerge>
              <feMergeNode />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
          
          {/* Background halo */}
          <radialGradient id="halo" cx="50%" cy="70%" r="60%">
            <stop offset="0%" stopColor="#FFFFFF" stopOpacity="0.45" />
            <stop offset="50%" stopColor="#E6F3FF" stopOpacity="0.2" />
            <stop offset="100%" stopColor="#FFFFFF" stopOpacity="0" />
          </radialGradient>
          
          {/* Blur glow filter */}
          <filter id="blurGlow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur in="SourceGraphic" stdDeviation="6" />
          </filter>
          
          {/* Glow filter for light show */}
          <filter id="lightGlow" x="-100%" y="-100%" width="300%" height="300%">
            <feGaussianBlur stdDeviation="8" result="coloredBlur"/>
            <feMerge>
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
        </defs>

        {/* Soft halo behind capsules */}
        <ellipse cx="370" cy="250" rx="250" ry="150" fill="url(#halo)" />

        {angles.map((angle, i) => {
          const r = 110;
          const cx = 370 + Math.cos((angle * Math.PI) / 180) * r;
          const cy = 325 + Math.sin((angle * Math.PI) / 180) * r;
          const rot = angle + 90;
          const body = colors[i] || colors[colors.length - 1];
          const sideOpacity = 0.5 + (i / (count - 1)) * 0.3;
          const progressWidth = (progress / 100) * 50;
          const cylinderHue = (lightHue + (i * 20)) % 120;
          
          return (
            <g key={i} transform={`translate(${cx}, ${cy}) rotate(${rot})`} filter="url(#soft)" className={isLoading ? "animate-cyl" : undefined}>
              {/* Glass body */}
              <rect x={-23} y={-92} rx={18} width={50} height={180} fill={body} stroke="#D8EDFF" strokeWidth={1.5} opacity={0.95} />
              
              {/* Inner core with enhanced depth */}
              <g transform="skewX(-10)">
                <rect x={-14} y={-75} rx={12} width={32} height={140} fill="url(#capsInner)" />
              </g>
              
              {/* Light show effect - red to green animation */}
              {isLoading && (
                <rect 
                  x={-23} 
                  y={-92} 
                  rx={18} 
                  width={50} 
                  height={180} 
                  fill={getHslColor(cylinderHue, 85, 65)}
                  opacity={0.5}
                  filter="url(#lightGlow)"
                />
              )}
              
              {/* Progress bar overlay - red to green light show */}
              {isLoading && progress > 0 && (
                <g>
                  {/* Progress fill with animated red-to-green gradient */}
                  <rect 
                    x={-23} 
                    y={-64} 
                    rx={18} 
                    width={Math.min(50, progressWidth)} 
                    height={152} 
                    fill={getHslColor(cylinderHue, 100, 70)}
                    opacity={0.95}
                    filter="url(#lightGlow)"
                  />
                  {/* Bright glowing edge at progress front */}
                  {progressWidth < 50 && (
                    <rect 
                      x={-23 + Math.min(50, progressWidth) - 2} 
                      y={-64} 
                      rx={18} 
                      width={6} 
                      height={152} 
                      fill={getHslColor(cylinderHue, 100, 85)}
                      opacity={1}
                      filter="url(#lightGlow)"
                    />
                  )}
                </g>
              )}
              
              {/* Enhanced white rounded cap with gradient */}
              <rect x={-23} y={-92} rx={18} width={50} height={28} fill="url(#capsTop)" opacity={0.98} />
              
              {/* Primary specular highlight */}
              <rect x={-23} y={-92} rx={18} width={22} height={160} fill="url(#capsSpec)" opacity={sideOpacity} />
              
              {/* Secondary highlight for extra gloss */}
              <rect x={-18} y={-85} rx={14} width={12} height={140} fill="url(#capsSpec2)" opacity={0.6} />
              
              {/* Top rim highlight for 3D effect */}
              <rect x={-20} y={-92} rx={16} width={40} height={12} fill="#FFFFFF" opacity={0.8} />
              
              {/* Bottom shadow for depth */}
              <rect x={-22} y={85} rx={17} width={46} height={8} fill="#1D83EB" opacity={0.15} />
            </g>
          );
        })}

        {/* Loading arc overlay with red-to-green light show */}
        {isLoading && (
          <g>
            {/* Main animated arc */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke={getHslColor(lightHue, 100, 60)}
              strokeWidth="12"
              strokeLinecap="round"
              fill="none"
              className="animate-dash"
              strokeDasharray="160 820"
              filter="url(#lightGlow)"
            />
            {/* Enhanced glow under the arc */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke={getHslColor(lightHue, 100, 65)}
              strokeWidth="18"
              strokeLinecap="round"
              opacity="0.4"
              filter="url(#blurGlow)"
              fill="none"
              className="animate-dash"
              strokeDasharray="160 820"
            />
            {/* Additional outer glow */}
            <path
              d="M120 250 A250 150 0 0 1 620 250"
              stroke={getHslColor(lightHue, 100, 70)}
              strokeWidth="22"
              strokeLinecap="round"
              opacity="0.2"
              filter="url(#blurGlow)"
              fill="none"
              className="animate-dash"
              strokeDasharray="160 820"
            />
          </g>
        )}
      </svg>

      {/* Animation keyframes */}
      {isLoading && (
        <style>
          {`
            @keyframes dash-move { 
              to { stroke-dashoffset: -980; } 
            }
            .animate-dash { 
              animation: dash-move 1.8s linear infinite; 
            }
            @keyframes cyl-pulse { 
              0% { 
                filter: brightness(1);
                transform: scale(1);
              } 
              50% { 
                filter: brightness(1.15);
                transform: scale(1.02);
              } 
              100% { 
                filter: brightness(1);
                transform: scale(1);
              } 
            }
            .animate-cyl { 
              animation: cyl-pulse 2s ease-in-out infinite; 
            }
          `}
        </style>
      )}
    </div>
  );
}
