// src/components/KPICard.tsx
import { motion } from "framer-motion";
import React, { useState } from "react";

interface KPICardProps {
  title: string;
  value: string;
  trend: string; // e.g., "+14.2% vs last month"
  icon?: React.ReactNode;
  onRefresh?: () => void;
  isRefreshing?: boolean;
  index?: number;
}

const KPICard = ({ title, value, trend, icon, onRefresh, isRefreshing, index = 0 }: KPICardProps) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.div
      initial={{ opacity: 0, y: 50, rotateX: -15 }}
      animate={{ opacity: 1, y: 0, rotateX: 0 }}
      transition={{
        duration: 0.6,
        delay: index * 0.1,
        type: "spring",
        stiffness: 100,
      }}
      whileHover={{
        y: -10,
        rotateY: 5,
        rotateX: 5,
        scale: 1.02,
        transition: { duration: 0.3 },
      }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      style={{
        perspective: 1000,
        transformStyle: "preserve-3d",
      }}
      className="relative w-full rounded-2xl p-6 min-h-[150px] bg-white shadow-sm border border-gray-100 cursor-pointer"
    >
      {/* 3D Glow Effect */}
      <motion.div
        animate={{
          opacity: isHovered ? 0.3 : 0,
          scale: isHovered ? 1.1 : 1,
        }}
        transition={{ duration: 0.3 }}
        className="absolute inset-0 rounded-2xl bg-gradient-to-br from-blue-400/20 to-purple-400/20 blur-xl -z-10"
      />

      {/* Top Section */}
      <div className="flex justify-between items-start mb-4">
        <motion.h3
          animate={{ scale: isHovered ? 1.05 : 1 }}
          className="text-sm font-medium text-gray-600"
        >
          {title}
        </motion.h3>
        <div className="flex items-center gap-2">
          {icon && (
            <motion.div
              animate={{ rotate: isHovered ? 360 : 0 }}
              transition={{ duration: 0.5 }}
              className="text-gray-400"
            >
              {icon}
            </motion.div>
          )}
          <motion.button
            whileHover={{ rotate: 180, scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className={`text-gray-400 hover:text-gray-600 text-sm ${isRefreshing ? "animate-spin" : ""}`}
            aria-label="Refresh"
            onClick={onRefresh}
            disabled={isRefreshing}
          >
            ‚ü≥
          </motion.button>
        </div>
      </div>

      {/* Value */}
      <motion.p
        animate={{ scale: isHovered ? 1.05 : 1 }}
        className="text-3xl font-semibold mb-2 text-gray-900"
      >
        {value}
      </motion.p>
      
      {/* Trend Indicator */}
      <motion.div
        animate={{ x: isHovered ? 5 : 0 }}
        className="flex items-center gap-1 text-sm"
      >
        <motion.svg
          animate={{ y: isHovered ? -2 : 0 }}
          className="w-4 h-4 text-green-500"
          fill="currentColor"
          viewBox="0 0 20 20"
        >
          <path fillRule="evenodd" d="M5.293 9.707a1 1 0 010-1.414l4-4a1 1 0 011.414 0l4 4a1 1 0 01-1.414 1.414L11 7.414V15a1 1 0 11-2 0V7.414L6.707 9.707a1 1 0 01-1.414 0z" clipRule="evenodd" />
        </motion.svg>
        <span className="text-green-600 font-medium">{trend}</span>
      </motion.div>

      {/* Shine effect on hover */}
      <motion.div
        animate={{
          x: isHovered ? ["-100%", "200%"] : "-100%",
        }}
        transition={{
          duration: 0.8,
          repeat: isHovered ? Infinity : 0,
          repeatDelay: 1,
        }}
        className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent rounded-2xl -z-0 pointer-events-none"
        style={{ transform: "skewX(-20deg)" }}
      />
    </motion.div>
  );
};

export default KPICard;
